      let res = ethJsUtil.fromRpcSig(validatorSignatures[i])
      let publicKey = ethJsUtil.ecrecover(ethJsUtil.toBuffer(ethers.utils.keccak256(testData.rlpBlockHeaderPreimage)), res.v, res.r, res.s);
