const ethEventDataStruct = {
  EventData: {
    index: 'uint256',
    signature: 'bytes32',
    logs: 'bytes',
  },
}

const ethBlockHeaderMetaStruct = {
  BlockHeaderMeta: {
    rlpBlockHeader: 'bytes',
    rlpBlockHeaderPreimage: 'bytes'
  }
}

const ethEncodedProofStruct = {
  Proof: {
    typ: 'uint256',
    ProofData: {
      witnesses: 'bytes',
      root: 'bytes32',
      blockHash: 'bytes32',
      blockHeaderMeta: 'bytes',
    },
    'Signature[]': {
      by: 'uint256',
      sigR: 'uint256',
      sigS: 'uint256',
      sigV: 'uint256',
      meta: 'bytes',
    }
  }
}
